--
-- Dumping routines for database 'TravelMaker'
--
/*!50003 SET @saved_sql_mode       = @@sql_mode */ ;
/*!50003 SET sql_mode              = 'STRICT_TRANS_TABLES,ERROR_FOR_DIVISION_BY_ZERO,NO_AUTO_CREATE_USER,NO_ENGINE_SUBSTITUTION' */ ;
/*!50003 DROP PROCEDURE IF EXISTS `GetAlbumsAndPhotos` */;
/*!50003 SET @saved_cs_client      = @@character_set_client */ ;
/*!50003 SET @saved_cs_results     = @@character_set_results */ ;
/*!50003 SET @saved_col_connection = @@collation_connection */ ;
/*!50003 SET character_set_client  = utf8mb4 */ ;
/*!50003 SET character_set_results = utf8mb4 */ ;
/*!50003 SET collation_connection  = utf8mb4_unicode_ci */ ;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `GetAlbumsAndPhotos`(IN `userToken` VARCHAR(255))
BEGIN
    SELECT
        a.album_id,
        a.album_name,
        a.tag,
        a.description,
        p.image_id,
        p.image_name,
        p.image_url
    FROM
        users as u
    JOIN albums as a ON u.user_id = a.user_id
    JOIN album_photos as p ON a.album_id = p.album_id
    WHERE
        u.token = userToken;
END ;;
DELIMITER ;
/*!50003 SET sql_mode              = @saved_sql_mode */ ;
/*!50003 SET character_set_client  = @saved_cs_client */ ;
/*!50003 SET character_set_results = @saved_cs_results */ ;
/*!50003 SET collation_connection  = @saved_col_connection */ ;
/*!50003 SET @saved_sql_mode       = @@sql_mode */ ;
/*!50003 SET sql_mode              = 'STRICT_TRANS_TABLES,ERROR_FOR_DIVISION_BY_ZERO,NO_AUTO_CREATE_USER,NO_ENGINE_SUBSTITUTION' */ ;
/*!50003 DROP PROCEDURE IF EXISTS `GetBoardData` */;
/*!50003 SET @saved_cs_client      = @@character_set_client */ ;
/*!50003 SET @saved_cs_results     = @@character_set_results */ ;
/*!50003 SET @saved_col_connection = @@collation_connection */ ;
/*!50003 SET character_set_client  = utf8mb4 */ ;
/*!50003 SET character_set_results = utf8mb4 */ ;
/*!50003 SET collation_connection  = utf8mb4_unicode_ci */ ;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `GetBoardData`(IN area_param VARCHAR(255))
BEGIN
    DECLARE record_count INT;
    
    SELECT COUNT(*) INTO record_count
    FROM board_text 
        JOIN users ON Posting_user_id = users.user_id 
        JOIN (
            SELECT *,
                   ROW_NUMBER() OVER (PARTITION BY board_text_id ORDER BY board_image_id) as rn
            FROM board_image
        ) board_image ON board_text.board_text_id = board_image.board_text_id
        WHERE Discussion_board_area = area_param AND board_image.rn = 1;

    IF record_count > 0 THEN
        -- 有資料的情況下執行原始查詢
        SELECT Discussion_board_area, board_text.board_text_id, board_text.Text_title, board_text.Text, board_text.Posting_time,
               users.full_name, users.user_name, board_image.image_path
        FROM board_text 
        JOIN users ON Posting_user_id = users.user_id 
        JOIN (
            SELECT *,
                   ROW_NUMBER() OVER (PARTITION BY board_text_id ORDER BY board_image_id) as rn
            FROM board_image
        ) board_image ON board_text.board_text_id = board_image.board_text_id
        WHERE Discussion_board_area = area_param AND board_image.rn = 1;
    ELSE
        -- 無資料的情況下執行另一個查詢或採取其他動作
        SELECT Discussion_board_area,
               board_text.board_text_id,
               board_text.Text_title,
               board_text.Text,
               board_text.Posting_time,
               users.full_name,
               users.user_name,
               NULL as image_path
        FROM board_text
        JOIN users ON board_text.Posting_user_id = users.user_id
        WHERE Discussion_board_area = area_param;
    END IF;
END ;;
DELIMITER ;
/*!50003 SET sql_mode              = @saved_sql_mode */ ;
